import { useCallback } from "react"
import { useEffect, useRef } from "react"
import { useCustomFetch } from "src/hooks/useCustomFetch"
import { SetTransactionApprovalParams } from "src/utils/types"
import { TransactionPane } from "./TransactionPane"
import { SetTransactionApprovalFunction, TransactionsComponent } from "./types"
import {Spinner} from 'spin.js';

export const Transactions: TransactionsComponent = ({ transactions }) => {
  const { fetchWithoutCache, loading } = useCustomFetch();
  const rawloader = useRef('');

  const opts = {
    length: 30,
    width: 10, 
    radius: 45, 
    scale: 1, 
    corners: 1, 
    speed: 1,
    rotate: 360,
    animation: 'spinner-line-fade-quick', 
    direction: 1, 
    color: 'blue',
    fadeColor: 'transparent', 
    top: '50%', 
    left: '50%', 
    shadow: '0 0 1px transparent', 
    zIndex: 2000000000, 
    className: 'spinner', 
    position: 'absolute', 
  };


  const spinner = new Spinner(opts);

 useEffect(() => {

  const loader = document.getElementById('RampLoading--container');
  spinner.spin(loader as HTMLElement);

 })



  const setTransactionApproval = useCallback<SetTransactionApprovalFunction>(
    async ({ transactionId, newValue }) => {
      await fetchWithoutCache<void, SetTransactionApprovalParams>("setTransactionApproval", {
        transactionId,
        value: newValue,
      })
    },
    [fetchWithoutCache]
  )

  if (transactions === null) {
    console.log('no result');
    return <div ref={rawloader} id="RampLoading--container" className="RampLoading--container"></div>

  }else{
    console.log('Result');
    spinner.stop();
    rawloader.current.style.display = 'none';
  }


  return (
    <div className="transaction-container" data-testid="transaction-container">
      {transactions.map((transaction) => (
        <TransactionPane
          key={transaction.id}
          transaction={transaction}
          loading={loading}
          setTransactionApproval={setTransactionApproval}
        />
      ))}
    </div>
  )
}
